onClick:
{{
  usuario_login.run(
    () => {
      const u = usuario_login.data[0];

      if (!u) {
        showAlert("Usuário ou senha inválidos", "error");
        return;
      }

      const roles = u.roles || [];
      const isAdmin = roles.includes("ADMIN");

      storeValue("user", u);
      storeValue("roles", roles);
      storeValue("filialId", u.id_filial);
      storeValue("isAdmin", isAdmin);

      navigateTo(isAdmin ? "Principal_adm" : "Principal_padrao");
    },
    (err) => {
      showAlert("Erro ao fazer login", "error");
      console.log(err);
    }
  );
}}
