customEChartConfig:
{{ ((chartType) => ( 
(() => {
  const rows = projeto_golive_grafico.data || [];
  if (!rows.length) return {};

  // formata data ISO -> DD/MM
  const fmt = (v) => {
    if (!v) return '';
    const d = new Date(v);
    if (isNaN(d)) return String(v);
    const dd = String(d.getDate()).padStart(2, '0');
    const mm = String(d.getMonth() + 1).padStart(2, '0');
    return `${dd}/${mm}`;
  };

  // eixo X: datas únicas ordenadas
  const dias = [...new Set(rows.map(r => fmt(r.dia)))];

  // legend / séries: setores únicos
  const setors = [...new Set(rows.map(r => r.setor))];

  const series = setors.map(setor => {
    const data = dias.map(d => {
      const item = rows.find(
        r => fmt(r.dia) === d && r.setor === setor
      );
      return item ? Number(item.total) : 0;
    });

    return {
      name: setor,
      type: 'line',
      stack: 'GoLive',
      areaStyle: {},
      emphasis: { focus: 'series' },
      data
    };
  });

  return {
    title: {
      text: 'Go-lives agendados por dia / setor',
      left: 'center'
    },
    tooltip: {
      trigger: 'axis',
      axisPointer: {
        type: 'cross',
        label: { backgroundColor: '#6a7985' }
      }
    },
    legend: {
      top: 40,
      type: 'scroll',
      data: setors
    },
    grid: {
      left: 15,
      right: 15,
      bottom: 30,
      top: 100,
      containLabel: true
    },
    xAxis: [
      {
        type: 'category',
        boundaryGap: false,
        data: dias      // ex.: ["10/12","11/12","12/12"...]
      }
    ],
    yAxis: [
      { type: 'value' }
    ],
    series
  };
})()
))(Chart1.chartType) }}

customFusionChartConfig:
{{
(() => {
  const rows = projeto_golive_grafico.data|| [];
  if (!rows.length) return {};

  // dias no eixo X
  const dias = [...new Set(rows.map(r => r.dia))].sort();

  // setores (cada um vira uma série)
  const setors = [...new Set(rows.map(r => r.setor))];

  // séries por setor (stacked area)
  const series = setors.map(s => ({
    name: s,
    type: 'line',
    stack: 'Total',
    areaStyle: {},
    emphasis: { focus: 'series' },
    data: dias.map(d => {
      const item = rows.find(r => r.dia === d && r.setor === s);
      return item ? Number(item.total) : 0;
    })
  }));

  return {
    title: { text: 'Go-lives agendados por dia / setor' },
    tooltip: {
      trigger: 'axis',
      axisPointer: {
        type: 'cross',
        label: { backgroundColor: '#6a7985' }
      }
    },
    legend: { data: setors },
    toolbox: { feature: { saveAsImage: {} } },
    xAxis: [{
      type: 'category',
      boundaryGap: false,
      data: dias
    }],
    yAxis: [{ type: 'value' }],
    series
  };
})()
}}
